import App from './../../App'
import {html, render } from 'lit-html'
import {gotoRoute, anchorRoute } from './../../Router'
import Auth from './../../Auth'
import Utils from './../../Utils'

class HomeView {
  init(){    
    console.log('HomeView.init')
    document.title = 'Home'    
    this.render()    
    Utils.pageIntroAnim()    
  }

  render(){
    const template = html`

    <style>

    :root {
      --body-color: white;
      --body-bg-color: blue;
    }


    section {
      padding: 100px 0;
    }

    .banner {
      background: url(/images/header2.jpg);
      padding: 50px;
      position: relative;
      min-height: 100vh;
      background-size: cover;
      background-position: right;
      display: flex;
      justify-content: space-between;
      align-items: center;
    }

    .banner h2 {
      font-size: clamp(30px, 5vw, 60px);
      color: white;
      font-weight: 500;
    }

    .banner h2 span {
      font-weight: 700;
    }

    .banner p {
      font-size: clamp(12px, 1vw, 2em);
      color: white;
      font-weight: 500;
    }

    sl-button {
      background-color: transparent;
    }

    /* My Work ------------------------------------------------------------------*/

        .my-work {
            color: black;
            text-align: center;    
        }


        .section__subtitle {
            margin-bottom: 8vh;
        }

        .portfolio {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
            grid-gap: 20px;
            margin-bottom: 7vh;
        }

        .portfolio__item {
            overflow: hidden;
        }

        .portfolio__img {
            transition: 
                transform 750ms cubic-bezier(.5, 0, .5, 1),
                opacity 250ms linear;
        }

        .portfolio__item:focus {
            position: relative;
            z-index: 2;
        }

        .portfolio__img:hover,
        .portfolio_item:focus .portfolio_img {
            opacity: .5;
        }

        .about {
          background-color: rgb(38, 38, 38);
          text-align: center;
        }

    </style>

      <va-app-header title="Home" user=${JSON.stringify(Auth.currentUser)}></va-app-header> 


      <section class="banner" id="home">
        <div class="textBx">
            <h2>Discover <br><span> Mauritius</span></h2>
            <p>Need some time for yourself? Tired of office and daily routines? <br>
            You have come at the right place ! Nature Warriors Mauritius brings you to places which will make your soul feel peace, 
            revitalize your brain and change your life. Join us !!</p>
            <sl-button size="medium" pill>Discover</sl-button>
        </div>
      </section>

      <!-- Gallery -->

        <section class="my-work" id="work">
            <h2 class="section_title section_title--work">Explore Mauritius</h2>
            <p class="section_subtitle section_subtitle--work"></p>

            <div class="portfolio">
                <!-- Portfolio item 01 -->
                <a href="portfolio-item.html" class="portfolio__item" target="_blank">
                    <img src="images/bleach.jpg" alt="" class="portfolio__img">
                </a>

                <!-- Portfolio item 02 -->
                <a href="portfolio-item.html" class="portfolio__item" target="_blank">
                    <img src="images/mountains.jpg" alt="" class="portfolio__img">
                </a>

                <!-- Portfolio item 03 -->
                <a href="portfolio-item.html" class="portfolio__item" target="_blank">
                    <img src="images/summer.jpg" alt="" class="portfolio__img">
                </a>

                <!-- Portfolio item 04 -->
                <a href="portfolio-item.html" class="portfolio__item" target="_blank">
                    <img src="images/walk.jpg" alt="" class="portfolio__img">
                </a>
            </div>

                <sl-button>View More Safe Hikes</sl-button>
        </section>

        <section class="about">
          <h6 style="color: white; font-weight: 300;padding:0 0 50px 0"><strong>WHO WE ARE <strong> /  WHAT WE BELIEVE / HOW WE WORK</h6>
          <h1 style="color:white;font-weight: 300; font-size: 36px;">About Nature Warriors</h1>
          <h6 style="color:white;padding:0 0 50px 0">Discover places where to hike safely.</h6>
          <p style="color:white; padding: 0 100px;">
          We propose different type of hikes that is Mountain Climbing, long walks
          in Nature and Coastal walks. As per your timeline, we adjust ourselves in
          order to propose the best trip to you and your family/friends/company.</p>


        </section>

    `
    render(template, App.rootEl)
  }
}

export default new HomeView()
